declare class Handler {
  id: string
  schedule: string
  handler: Function
  timeout: number
  constructor(id: string, schedule: string, handler: Function, timeout: number)
  invoke(): any
}

export declare interface Schedule {
  use(schedule: string, ...args: any[]): Handler | null
  every(expression: string, ...args: any[]): Handler | null
  cron(expression: string, ...args: any[]): Handler | null
  rate(expression: string, ...args: any[]): Handler | null
}

declare const schedule: Schedule
export default schedule
