"use strict";

// src/vite.ts
module.exports = function withCloud(options = {}) {
  options.ssr = options.ssr || {};
  options.ssr.external = ["@serverless/cloud"].concat(options.ssr.external || []);
  options.build = options.build || {};
  options.build.rollupOptions = options.build.rollupOptions || {};
  options.build.rollupOptions.external = ["@serverless/cloud"].concat(options.build.rollupOptions.external || []);
  return options;
};
